mapper.select:
	SELECT * FROM (
    SELECT ROWNUM RNUM, DATA.* FROM (
        SELECT NUM, NAME, SUBJECT, HITCOUNT, TO_CHAR(CREATED, 'YYYY-MM-DD') CREATED 
        FROM QNA
        WHERE ${searchKey} LIKE '%' || #{searchValue} || '%'
        ORDER BY NUM DESC
    ) DATA
)

	WHERE RNUM >= #{start} AND RNUM <= #{end}
